def func(sentence:str,label:str):
    from flair.data import Sentence
    tagger=get_plugin("tag")
    s= Sentence(sentence)
    tagger.predict(s)
    labels=label.split(',')
    __result__={}
    for one in labels:
        __result__[one ]=[]
        for entity in s.get_spans('ner'):
            if entity.tag==one :
                __result__[one ].append(entity.text)
    return __result__
